mapper.select:
select *
from dormitory_management_system.view_get_all_clazz_list

    and name like concat('%',#{map.name},'%')
    and stu_number>=#{map.stuNumber}
    and ins_name=#{map.insName}
    and major_name=#{map.majorName}
    and entrance_year=#{map.entranceYear}


    order by ${prop} ${order}

mapper.update:
update dormitory_management_system.clazz
set name=#{name}
where id=#{id}
